wwv_flow_api.create_page_plug(
 p_id=>wwv_flow_api.id(70109607452963367938)
,p_plug_name=>'Notes'
,p_region_template_options=>'#DEFAULT#:js-useLocalStorage:is-collapsed:t-Region--scrollBody'
