,p_javascript_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'// create a private scope where $ is set to apex.jQuery',
'(function($) {',
'    // This function processes the current selection. It increases the salary ',
'    // field of each selected row by the given percent',
'    function increaseSalary(percent) {',
'        var i, records, record, sal, model,',
'            view = apex.region("emp").widget().interactiveGrid("getCurrentView");',
'',
'        if ( view.supports.edit ) { // make sure this is the editable view',
'            percent = percent / 100;',
'            model = view.model;',
'            records = view.getSelectedRecords();',
'            for ( i = 0; i < records.length; i++ ) {',
'                record = records[i];',
'                sal = parseFloat(model.getValue(record, "SAL"));',
'                if ( !isNaN(sal) ) {',
'                    sal = sal + sal * percent;',
'                    model.setValue(record, "SAL", "" + sal);',
'                }',
'            }',
'        }',
'',
'    }',
'    // expose this function so it can be called from the Increase Salary ',
'    // dialog OK button DA. Normally you would put it in your own app/page namespace.',
'    window.increaseSalary = increaseSalary;',
'',
'    // do this after the page loads but before the IG is initialized to catch the initial events',
'    $(function() {',
'        //',
'        // This is the general pattern for adding to the grid view row or selection menus',
'        //',
'        // listen for view change events to find out when grid views are created ',
'        $("#emp").on("interactivegridviewchange", function(event, data) {',
'            if ( data.view === "grid" && data.created ) {',
'                var view = apex.region("emp").widget().interactiveGrid("getViews", "grid"),',
'                    menu$ = view.selActionMenu$;',
'                    // to modify the row action menu use view.rowActionMenu$',
'',
'                // now add items to the menu that reference actions added to the IG',
'                // see IG attribute Advanced JavaScript Code',
'                menu$.menu("option").items.push({',
'                    type:"action",',
'                    action: "selection-increase-salary"',
'                });',
'            }',
'        });',
'    });    ',
'})(apex.jQuery);',
''))
