,p_javascript_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'// there is way to much code so using minimal indenting',
'function(config) {',
'var $ = apex.jQuery,',
'  toolbarData = $.apex.interactiveGrid.copyDefaultToolbar(),',
'  toolbarGroup = toolbarData.toolbarFind("actions3"), // the group with add row button',
'  addBtn = toolbarGroup.controls[0]; // assume add row button is first and only in this group',
'',
'// Can set the label for the add row button but can''t include markup for icons etc.',
'// So update the button properties in the toolbar',
'addBtn.icon = "t-Icon fa fa-plus";',
'addBtn.iconBeforeLabel = true;',
'// or try this in place of iconBeforeLabel',
'//addBtn.iconOnly = true;',
'',
'// add the move up/down buttons',
'toolbarGroup.controls.unshift({',
'  type: "BUTTON",',
'  iconOnly: true,',
'  icon: "icon-up-chevron",',
'  action: "selection-move-up"',
'} );',
'toolbarGroup.controls.unshift({',
'  type: "BUTTON",',
'  iconOnly: true,',
'  icon: "icon-down-chevron",',
'  action: "selection-move-down"',
'});',
'config.toolbarData = toolbarData;',
'',
'// The shortcuts using Alt+Up/Down don''t work because the grid widget key handling',
'// kicks in before the action key handling. Could pick other keys besides arrows.',
'// add the move up/down actions',
'config.initActions = function(actions) {',
'  var save = actions.lookup("save"),',
'      saveFn = save.action;',
'',
'  // this is part of the hack to avoid the "no data found" error on save',
'  save.action = function() {',
'      if ($v("P11_SHOW_COMPLETED") === "N") {',
'          $s("P11_SAVING", "Y");',
'      }',
'      // do original save',
'      saveFn();',
'  };',
'  actions.add( [',
'  {',
'      name: "selection-move-up",',
'      label: "Move Up",',
'//    shortcut: "Alt+Up",',
'      action: function() {',
'        var after$,',
'          after = null,',
'          view = $(actions.context).interactiveGrid("getViews").grid;',
'          grid$ = view.view$,',
'          model = view.model,',
'          rows = grid$.grid( "getSelection" ),',
'          items = grid$.grid( "getSelectedRecords" );',
'',
'        if ( rows[0].prev().length ) {',
'          after$ = rows[0].prev().prev();',
'          if ( after$.length ) {',
'              after = grid$.grid( "getRecords", [after$] )[0];',
'          }',
'          model.moveRecords( items, null, after );',
'          grid$.grid( "setSelectedRecords", items );',
'        }',
'      }',
'  },',
'  {',
'      name: "selection-move-down",',
'      label: "Move Down",',
'//    shortcut: "Alt+Down",',
'      action: function() {',
'        var after$,',
'          after = null,',
'          view = $(actions.context).interactiveGrid("getViews").grid;',
'          grid$ = view.view$,',
'          model = view.model,',
'          rows = grid$.grid( "getSelection" ),',
'          items = grid$.grid( "getSelectedRecords" );',
'',
'        after$ = rows[rows.length - 1].next();',
'        if ( after$.length ) {',
'          after = grid$.grid( "getRecords", [after$] )[0];',
'          model.moveRecords( items, null, after );',
'          grid$.grid( "setSelectedRecords", items );',
'        }',
'      }',
'  },',
'  {',
'      name: "selection-complete",',
'      label: "Mark Complete",',
'      action: function() {',
'        var i,',
'          view = $(actions.context).interactiveGrid("getViews").grid;',
'          grid$ = view.view$,',
'          model = view.model,',
'          records = grid$.grid( "getSelectedRecords" );',
'',
'        for ( i = 0; i < records.length; i++ ) {',
'          model.setValue( records[i], "DONE", "Y" );',
'        }',
'      }',
'  }',
'  ] );',
'  $(actions.context).on("interactivegridselectionchange", function(event, data) {',
'    var method = "enable";',
'    if ( data.selectedRecords.length === 0 ) {',
'      method = "disable";',
'    }',
'    actions[method]("selection-complete");',
'    actions[method]("selection-move-down");',
'    actions[method]("selection-move-up");',
'  });',
'};',
'// let the model manage the sequence column',
'config.defaultModelOptions = {',
'  sequenceField: "SEQ",',
'  sequenceStep: 10',
'};',
'return config;',
'}'))
);
