,p_autocomplete_on_off=>'OFF'
,p_javascript_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'// make sure this goes in function and global variable declaration so it happens before the IG is initialized',
'apex.jQuery(function() {',
'    apex.item.create("C_ONLEAVE", {',
'        displayValueFor: function(value) {',
'            // not accessible but could be by having the value be the display value',
'            // or using client side text messages or hard coded text. ',
'            // Either way include the text in a aria-labeledby or title etc.',
'            return ''<span aria-hidden="true" class="fa '' + ',
'                (value === "Y" ? "fa-check-square-o" : "fa-square-o") + ',
'                ''"></span>'';',
'        }',
'    });',
'});',
''))
