,p_step_sub_title=>'Edit in Dialog'
,p_step_sub_title_type=>'TEXT_WITH_SUBSTITUTIONS'
,p_autocomplete_on_off=>'OFF'
,p_javascript_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'(function($) {',
'',
'function createEmpEditDialog() {',
'    // destroy any previous dialog if any',
'    if ( window.gEmpEditDialog$ ) {',
'        window.gEmpEditDialog$.remove();',
'    }',
'',
'    // Danger Not sure if modelColumns and specialColumns will end up being part of the public view interface',
'    var view = apex.region("emp").widget().interactiveGrid("getViews", "grid");',
'    var fields = $.extend(true, {}, view.modelColumns);',
'    delete fields.Actions;',
'    // TODO may want to explicitly control the order of fields or make other adjustments',
'',
'    // It would be nice if createModelEditDialog let you customize the label for the save button',
'    // and automatically disabled it if the model is not editable.',
'    empEditDialog$ = $.apex.recordView.createModelEditDialog( "empEditDialog", {',
'        width: 380,',
'        height: 560,',
'        title: "Employee",',
'        modelName: "editEmp",',
'        labelAlignment: "start",',
'        modelOptions: {',
'            shape: "record",',
'            recordIsArray: true,',
'            editable: true,',
'            identityField: view.specialColumns.identityField,',
'            fields: fields',
'        },',
'        load: function( model ) {',
'            var i,',
'                record =  $( this ).dialog( "option", "record" ),',
'                editable = $( this ).dialog( "option", "editable" ),',
'                newRecord = [];',
'',
'            $(this).parent().find("button.ui-button--hot").toggle(editable); // hide save button if needed',
'            model.setOption("editable",editable);',
'',
'            // the dialog model and the IG model share essentially the same field config',
'            // so record can just be copied',
'            for ( i = 0; i < record.length; i++ ) {',
'                newRecord[i] = record[i];',
'            }',
'            model.setData( newRecord );',
'        },',
'        store: function( model ) {',
'            var f, field, id,',
'                record =  $( this ).dialog( "option", "record" ),',
'                newRecord = model.getRecord();',
'',
'            for ( f in fields ) {',
'                if ( fields.hasOwnProperty(f) && f !== "_meta" ) {',
'                    field = fields[f];',
'                    if ( !field.readonly && !field.virtual ) {',
'                        view.model.setValue(record, f, newRecord[fields[f].index]);',
'                        // Assume that the dialog wouldn''t allow save if any values are invalid so clear any errors',
'                        // in case there were some from add row.',
'                        id = view.model.getRecordId(record);',
'                        view.model.setValidity( "valid", id, f );',
'                    }',
'                }',
'            }',
'        }',
'    });',
'',
'    // expose this for use by row-edit action',
'    window.gEmpEditDialog$ = empEditDialog$;',
'}',
'',
'// need to do this here rather than in Execute when Page Loads so that the handler',
'// is setup BEFORE the IG is initialized otherwise miss the first model created event',
'$(function() {',
'    var region$ = $("#emp");',
'    // the model gets released and created at various times such as when the report changes',
'    // listen for model created events so that we can create a dialog with same config as IG model',
'    region$.on("interactivegridviewmodelcreate", function(event, ui) {',
'        var sid, item, gridInst, view,',
'            model = ui.model;',
'        if ( ui.viewId === "grid" ) {',
'            createEmpEditDialog();',
'        }',
'    });',
'',
'    //',
'    // This is the general pattern for getting access to the views as they are created',
'    //',
'    $("#emp").on("interactivegridviewchange", function(event, data) {',
'        if ( data.view === "grid" && data.created ) {',
'            var view = apex.region("emp").widget().interactiveGrid("getViews", "grid");',
'',
'            // Do this here because it can''t be set through the IG widget at init time',
'            view.view$.grid("option", "allowEditMode", false);',
'        }',
'    });',
'});    ',
'',
'})(apex.jQuery);',
''))
