'    view = apex.region("emp").widget().interactiveGrid("getCurrentView");',
'',
'var MULTIPLE_VALUES = { v:"", d:"" };',
'',
'function same(v1, v2) {',
'    if ( v1.v !== undefined ) {',
'        v1 = v1.v;',
'    }',
'    if ( v2.v !== undefined ) {',
'        v2 = v2.v;',
'    }',
'    return v1 == v2; // intentional use of ==',
'}',
'',
'if ( !view.supports.edit ) { // make sure this is the editable view TODO shouldn''t any view that supports selection be OK?',
'    return; // not expected. If views other than grid are possible then more needs to be done',
'}',
'',
'model = view.model;',
'records = view.getSelectedRecords();',
'',
'if ( !records.length ) {',
'    // nothing selected so clear details',
'    $("#details").find(".container").hide();',
'    $("#nothingSelected").show();',
'    gP26_editableSelection = null;',
'    return;',
'}',
'',
'// something selected so show details',
'$("#details").find(".container").show();',
'$("#nothingSelected").hide();',
'',
'columns = view.view$.grid("getColumns");',
'',
'// Danger: this gets acess to private methods of base class.',
'// todo consider exposing these methods',
'gridInst = view.view$.data("apex-grid");',
'',
'// This assumes the model uses an array for a record which is likely to remain true for IG.',
'compositeRecord = [];',
'compositeMeta = {};',
'columnItems = [];',
'itemContainers = [];',
'editable = true; // assume editable until any record isn''t',
'for ( i = 0; i < records.length; i++ ) {',
'    record = records[i];',
'    if ( editable && !model.allowEdit( record ) ) {',
'        editable = false;',
'    }',
'    if ( editable ) {',
'        id = model.getRecordId( record );',
'        meta = model.getRecordMetadata( id );',
'        // TODO merge into composite meta. This is only to support disabled fields',
'    }',
'    if ( i === 0 ) {',
'        // gather columnItem and initial composite value',
'        for ( j = 0; j < columns.length; j++ ) {',
'            prop = columns[j].property;',
'            index = columns[j].index;',
'            columnItem = apex.item("P26_" + prop);',
'            if ( columnItem.node ) {',
'                columnItems[j] = columnItem;',
'                itemContainers[j] = $(columnItem).closest(".t-Form-fieldContainer");',
'                compositeRecord[index] = model.getValue( record, prop );',
'            }',
'        }',
'    } else {',
'        for ( j = 0; j < columns.length; j++ ) {',
'            if ( columnItems[j] ) {',
'                prop = columns[j].property;',
'                index = columns[j].index;',
'                value = model.getValue( record, prop );',
'                if ( compositeRecord[index] !== MULTIPLE_VALUES && !same( value, compositeRecord[index] ) ) {',
'                    compositeRecord[index] = MULTIPLE_VALUES;',
'                }',
'            }',
'        }',
'    }',
'}',
'record = compositeRecord;',
'meta = compositeMeta;',
'',
'// load all values',
'gridInst._beginSetColumnItems();',
'for ( j = 0; j < columns.length; j++ ) {',
'    prop = columns[j].property;',
'    index = columns[j].index;',
'    columnItem = columnItems[j];',
'    if ( columnItem ) {',
'        // issue: not all items can be disabled!',
'        columnItem[ editable ? "enable" : "disable" ]();',
'        $(columnItem.node).closest(".t-Form-fieldContainer").toggleClass("is-multiValued", record[index] === MULTIPLE_VALUES );',
'        gridInst._setColumnItemValue( columnItem, record, prop, editable ? meta : null );',
